'use client';

import { Separator } from "@/components/ui/separator";
import { Award, ShieldCheck } from "lucide-react";
import Image from "next/image";

interface CertificateProps {
  studentName: string;
  studentClass: string;
  subject: string;
  score: number;
  totalQuestions: number;
  percentage: number;
  performanceStatus: 'पास' | 'औसत' | 'फेल';
}

const directorPhoto = 'data:image/jpeg;base64,';

export function TestResultCertificate({
  studentName,
  studentClass,
  subject,
  score,
  totalQuestions,
  percentage,
  performanceStatus,
}: CertificateProps) {
  const currentDate = new Date().toLocaleDateString('hi-IN', {
    day: 'numeric',
    month: 'long',
    year: 'numeric',
  });

  const getStatusInfo = () => {
    switch (performanceStatus) {
      case 'पास':
        return { text: 'उत्तीर्ण (Pass)', color: 'text-green-600' };
      case 'औसत':
        return { text: 'औसत (Average)', color: 'text-yellow-600' };
      case 'फेल':
        return { text: 'अनुत्तीर्ण (Failed)', color: 'text-red-600' };
      default:
        return { text: '', color: '' };
    }
  };

  const statusInfo = getStatusInfo();
  
  return (
    <div className="w-full max-w-3xl bg-white shadow-2xl rounded-lg border-4 border-blue-800 p-2 font-body">
      <div className="w-full h-full border-2 border-blue-700 p-6 relative flex flex-col">
        {/* Background Watermark */}
        <div className="absolute inset-0 flex items-center justify-center">
            <ShieldCheck className="w-64 h-64 text-blue-100/50 opacity-20" />
        </div>

        {/* Header */}
        <div className="text-center z-10">
            <h1 className="text-4xl font-bold font-headline text-blue-900">गो स्वामी डिफेंस एकेडमी</h1>
            <p className="font-semibold text-blue-800/90">"राष्ट्र प्रथम, शिक्षा सर्वोपरि"</p>
            <p className="text-sm text-gray-600 mt-1">खड़गपुर, धौलपुर (राज.) - 328023</p>
        </div>

        {/* Title */}
        <div className="text-center my-6 z-10">
            <p className="text-2xl font-bold tracking-widest text-gray-700">AI टेस्ट प्रदर्शन प्रमाण पत्र</p>
            <p className="text-base text-gray-500 mt-2">CERTIFICATE OF ACHIEVEMENT</p>
        </div>

        {/* Main Content */}
        <div className="text-center my-4 flex-grow z-10">
            <p className="text-lg text-gray-800">यह प्रमाणित किया जाता है कि</p>
            <p className="text-5xl font-bold font-headline text-blue-800 my-3">{studentName}</p>
            <p className="text-lg text-gray-800">
                कक्षा <strong className="font-semibold">{studentClass}</strong> ने <strong>{subject}</strong> विषय के मॉक टेस्ट में सफलतापूर्वक भाग लिया।
            </p>
        </div>

        {/* Results Section */}
        <div className="grid grid-cols-3 gap-4 text-center my-6 z-10">
            <div>
                <p className="text-3xl font-bold text-blue-900">{score}/{totalQuestions}</p>
                <p className="text-sm text-gray-600 font-semibold">अंक (Score)</p>
            </div>
            <div>
                <p className="text-3xl font-bold text-blue-900">{percentage.toFixed(2)}%</p>
                <p className="text-sm text-gray-600 font-semibold">प्रतिशत (Percentage)</p>
            </div>
            <div>
                <p className={`text-3xl font-bold ${statusInfo.color}`}>{statusInfo.text}</p>
                <p className="text-sm text-gray-600 font-semibold">परिणाम (Result)</p>
            </div>
        </div>

        {/* Footer */}
        <div className="mt-auto pt-6 flex justify-between items-end z-10">
            <div className="text-center">
                <p className="font-semibold">{currentDate}</p>
                <Separator className="my-1 bg-gray-400"/>
                <p className="text-sm text-muted-foreground">दिनांक (Date)</p>
            </div>
             <div className="text-center">
                <Image src="/signature.png" alt="Signature" width={150} height={50} />
                <Separator className="my-1 bg-gray-400"/>
                <p className="text-sm text-muted-foreground">निदेशक (Director)</p>
            </div>
             <div className="w-24 h-24 relative">
                <Image src={directorPhoto} alt="Director Photo" layout="fill" objectFit="cover" className="rounded-md border-2 border-gray-300" />
            </div>
        </div>
      </div>
    </div>
  );
}
